import React from "react";

export default function SplashScreen() {
  return (
    <section className="bg-purple-100 dark:bg-gray-800 p-10 rounded-xl text-center my-6">
      <h2 className="text-4xl font-bold mb-4 text-purple-700 dark:text-white">Welcome to Crypto Insight!</h2>
      <p className="text-lg text-gray-700 dark:text-gray-300">Get real-time market insights, track your portfolio, and connect with BNB Chain wallets.</p>
    </section>
  );
}
