export default function WalletConnect() {
  return (
    <section className="bg-blue-100 dark:bg-gray-800 p-6 rounded-xl my-6 text-center">
      <h3 className="text-xl font-semibold mb-2 text-blue-700 dark:text-white">Wallet Connection</h3>
      <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700">
        Connect Wallet
      </button>
    </section>
  );
}
